	#include <bits/stdc++.h>	#include <cstdio>	#include <cstring>	#include <cmath>	#include <cstring>	#include <chrono>	#include <complex>	#define endl "\n"	#define ll long long int	#define vi vector<int>	#define vll vector<ll>	#define vvi vector < vi >	#define pii pair<int,int>	#define pll pair<long long, long long>	#define mod 1000000007	#define inf 1000000000000000001;	#define all(c) c.begin(),c.end()	#define mp(x,y) make_pair(x,y)	#define mem(a,val) memset(a,val,sizeof(a))	#define eb emplace_back	#define f first	#define s second		using namespace std;	int main()	{		std::ios::sync_with_stdio(false);		ll n,m,k;		cin >> n >> m >> k;		ll l = 1;		ll r = m*n + 1;		if(l == r){			cout << 1 << endl;			return 0;		}		ll mid;		while(l < r){			mid = (l + r)/2;			ll count = 0;			for(ll i = 1 ; i <= n ; i++){				count += min((mid-1)/i,m);			}			if(count < k){				l = mid + 1;			}			else r = mid;		}		cout << l-1 << endl;		return 0;	}