#include <bits/stdc++.h>#define fio ios_base::sync_with_stdio(0);cin.tie(0);cout.tie(0)using namespace std;typedef long long int lli;const int MM = 1200000;lli m,n,k,p1,p2,p3,p,f,a[MM];string s,s1,s2;bool chk(lli val){    lli ans = 0;    for(int i=1; i<=n; i++)        ans += min(val/i, m);    return (ans >= k);}int main(){ // remove during scanf              cin >> n >> m >> k;    lli hi = n*m;    lli lo = 1;    lli mid;    while(hi > lo)    {        mid = (hi + lo)/2;        if(chk(mid))            hi = mid;        else            lo = mid+1;    }    cout << lo;    return 0;}