#include <bits/stdc++.h>#define fio ios_base::sync_with_stdio(0);cin.tie(0);cout.tie(0)using namespace std;typedef long long int lli;const int MM = 2100;lli m,n,k,p1,p2,p3,p,f,a[MM];string s,s1,s2;const lli mod = 1e9 + 7;lli dp[MM][MM];int main(){fio; // remove during scanf              cin >> n >> k;    for(int i=1; i<=n; i++)        dp[i][1] = 1;    for(int siz = 1; siz < k; siz++)        for(int i=1; i<=n; i++)            for(int j=i; j<=n; j += i)                dp[j][siz+1] = (dp[j][siz+1] + dp[i][siz]) % mod;            lli ans = 0;    for(int i=0; i<=n; i++)        ans += dp[i][k];    cout << ans % mod;    return 0;}